CREATE TABLE `player_settings` (`USER_ID` bigint(20) NOT NULL, `GUILD_ID` bigint(20) NOT NULL, `WORD_COUNT` smallint(6) NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `server_settings` (`GUILD_ID` bigint(20) NOT NULL, `CHANNEL_ID` bigint(20) DEFAULT NULL, `LOCALE` varchar(255) NOT NULL DEFAULT 'FR', `LEADERBOARD_ENABLED` tinyint(1) NOT NULL DEFAULT '1', `LEADERBOARD_TYPE` varchar(255) NOT NULL DEFAULT 'ALL') ENGINE=InnoDB DEFAULT CHARSET=latin1;
ALTER TABLE `server_settings` ADD UNIQUE KEY `GUILD_ID` (`GUILD_ID`); COMMIT;